import React, { Component } from "react";
import { Link } from "react-router-dom";

class dashboard extends Component {
  constructor(pros) {
    super(pros);

    this.checkUser = this.checkUser.bind(this);
    this.onChange = this.onChange.bind(this);
  }

  onChange = e => {
    this.setState({ [e.target]: e.target.value });
  };

  componentDidMount() {
    this.checkUser();
  }

  componentDidUpdate() {
    this.checkUser();
  }
  checkUser() {
    try {
      var userType = JSON.parse(localStorage["token"]).user_type;
      if (userType == "Customer") {
        return true;
      } else {
        return false;
      }
    } catch (e) {}
  }
  render() {
    return (
      <div className="app">
        <div>
          <h1>DASHBOARD</h1>
          {this.checkUser() && (
            <h1 className="header" onChange={this.onChange}>
              Welcome {JSON.parse(localStorage["token"]).user_name}
            </h1>
          )}
          {!this.checkUser() && <h1>This is vendor</h1>}
          <Link to="/logout">Logout</Link>
        </div>
      </div>
    );
  }
}

export default dashboard;
