import React, { Component } from "react";
import { InputGroup, FormControl, Form, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import Axios from "axios";

class UserDashboard extends Component {
  constructor(props) {
    super(props);

    this.state = {
      search: "",
      items: []
    };

    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };

  onSubmit = e => {
    e.preventDefault();

    Axios.get(
      "http://localhost:4000/product/fetch/" + this.state.search.toLowerCase()
    )
      .then(resp => {
        this.setState({ items: resp.data });
        console.log(this.state.items);
      })
      .catch(e => {
        console.log(e);
      });
  };

  checkUser() {
    try {
      var userType = JSON.parse(localStorage["token"]).login;
      if (userType === true) {
        return true;
      } else {
        return false;
      }
    } catch (e) {
      this.props.history.push("login");
    }
  }
  render() {
    return (
      <div>
        <div className="row d-flex justify-content-center mt-4">
          <h2>User Dashboard</h2>
        </div>
        <div>
          <div className="row d-flex justify-content-center mt-4">
            <h3>Welcome User {JSON.parse(localStorage["token"]).user_name}</h3>
            <div className="row d-flex justify-content-right ml-5 mt-1  ">
              <Link to="/logout">logout</Link>
            </div>

            <br />
          </div>
          <Form
            inline
            className="row d-flex justify-content-center mt-5"
            onSubmit={this.onSubmit}
          >
            <FormControl
              type="text"
              id="search"
              placeholder="Search"
              className="mr-sm-2"
              onChange={this.onChange}
              value={this.state.value}
            />
            <Button variant="outline-primary">Search</Button>
          </Form>
          <br />
        </div>
      </div>
    );
  }
}

export default UserDashboard;
