import React, { Component } from "react";
import { Link } from "react-router-dom";

class dashboard extends Component {
  constructor(pros) {
    super(pros);

    this.checkUser = this.checkUser.bind(this);
    this.onChange = this.onChange.bind(this);
    this.checkToken = this.checkToken.bind(this);
  }

  onChange = e => {
    this.setState({ [e.target.className]: e.target.value });
  };

  componentDidMount() {
    try {
      this.checkUser();
      this.checkToken();
    } catch (e) {
      console.log(e);
    }
  }

  componentDidUpdate() {
    this.checkUser();
  }

  checkToken() {
    try {
      var userType = JSON.parse(localStorage["review"]);
      return true;
    } catch (e) {
      this.props.history.push("login");
    }
  }
  checkUser() {
    try {
      var userType = JSON.parse(localStorage["token"]).user_type;
      if (userType == "Customer") {
        return true;
      } else {
        return false;
      }
    } catch (e) {
      this.props.history.push("login");
    }
  }
  render() {
    {
      this.checkUser() && this.checkToken();
    }
    return (
      <div className="app">
        <div>
          <label>Product Name : </label>
          <input
            name="order"
            readOnly
            value={JSON.parse(localStorage["review"]).item_name}
          ></input>
        </div>
        <div>
          <label>Seller Name : </label>
          <input
            name="seller"
            readOnly
            value={JSON.parse(localStorage["review"]).seller_id}
          ></input>
        </div>
      </div>
    );
  }
}

export default dashboard;
