import React, { Component } from "react";
import { Link } from "react-router-dom";
import Axios from "axios";

class review extends Component {
  constructor(pros) {
    super(pros);

    this.state = {
      seller_name: "",
      ratings: [],
      rating_avail: false
    };
    this.onChange = this.onChange.bind(this);
  }

  fetchRatings(e) {
    Axios.get(
      "http://localhost:4000/ratings/" +
        JSON.parse(localStorage["seller"]).seller_name
    )
      .then(resp => {
        if (Object.keys(resp.data).length === 0) {
          this.setState({ ratings: "" });
          alert("No item found !");
        } else {
          this.setState({ ratings: resp.data });
          if (Object.keys(this.state.items).length != 0) {
            this.setState({ rating_avail: true });
          }
        }
      })
      .catch(e => {
        console.log(e);
      });
  }

  onChange = e => {
    this.setState({ [e.target.className]: e.target.value });
  };

  componentDidMount() {
    try {
      this.setState({
        seller_name: JSON.parse(localStorage["seller"]).seller_name
      });
    } catch (e) {
      this.props.history.push("login");
      console.log(e);
    }
  }

  render() {
    return (
      <div className="app">
        <div className="mt-5">
          <div className="row d-flex justify-content-center mt-5">
            <h3>SELLER DETAILS</h3>
          </div>
          <div>
            <br></br>
            <div className="row d-flex justify-content-center mt-5">
              <h3>SELLER NAME</h3>
              <h3 className="ml-4">{this.state.seller_name}</h3>
            </div>

            <div>
              <table>
                <tr>
                  <th>Ratings</th>
                </tr>
                <tbody>
                  {this.state.items.map((item, i) => {
                    return (
                      <tr>
                        <td className="mt-5">{item.name}</td>
                        <td className="mt-5">
                          <a
                            href="/seller"
                            onClick={this.checkSeller(item.seller)}
                          >
                            {item.seller}
                          </a>
                        </td>
                        <td className="mt-5">{item.left_quantity}</td>
                        <td className="mt-5">{item.price_pc}</td>
                        <td className="mt-5"></td>
                        <td className="mt-5">
                          <Button
                            type="submit"
                            value={item._id}
                            onClick={this.buyItem}
                          >
                            Buy Now
                          </Button>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default review;
