import React, { Component } from "react";
import { InputGroup, FormControl, Form, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import Axios from "axios";

class UserDashboard extends Component {
  constructor(props) {
    super(props);

    this.state = {
      search: "",
      items: [],
      submit: false
    };

    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.validateProducts = this.validateProducts.bind(this);
    this.sortProduct = this.sortProduct.bind(this);
    this.listAll = this.listAll.bind(this);
  }

  onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };

  listAll = e => {
    e.preventDefault();

    this.setState({ submit: false });

    if (this.state.search == "") {
      alert("Please enter item to search !");
      return;
    }

    Axios.get("http://localhost:4000/product/fetchall")
      .then(resp => {
        if (Object.keys(resp.data).length === 0) {
          this.setState({ search: "" });
          alert("No item found !");
        } else {
          this.setState({ items: resp.data });
          this.validateProducts();
          if (Object.keys(this.state.items).length != 0) {
            this.setState({ submit: true });
          }
        }
      })
      .catch(e => {
        console.log(e);
      });
  };

  onSubmit = e => {
    e.preventDefault();

    this.setState({ submit: false });

    Axios.get(
      "http://localhost:4000/product/fetch/" + this.state.search.toLowerCase()
    )
      .then(resp => {
        if (Object.keys(resp.data).length === 0) {
          this.setState({ search: "" });
          alert("No item found !");
        } else {
          this.setState({ items: resp.data });
          this.validateProducts();
          if (Object.keys(this.state.items).length != 0) {
            this.setState({ submit: true });
          }
        }
      })
      .catch(e => {
        console.log(e);
      });
  };

  sortProduct(e) {
    var newArray = this.state.items.slice();
    newArray.sort((a, b) => b[e.target.value] - a[e.target.value]);
    console.log(newArray);
    this.setState({ items: newArray });
  }

  validateProducts() {
    var temp_arr = [];
    for (var i = 0; i < this.state.items.length; i++) {
      if (this.state.items[i].quantity > 0) {
        temp_arr.push(this.state.items[i]);
      }
    }

    this.setState({ items: temp_arr });
  }

  checkUser() {
    try {
      var userType = JSON.parse(localStorage["token"]).login;
      if (userType === true) {
        return true;
      } else {
        return false;
      }
    } catch (e) {
      this.props.history.push("login");
    }
  }
  render() {
    return (
      <div>
        <div className="row d-flex justify-content-center mt-4">
          <h2>User Dashboard</h2>
        </div>
        <div>
          <div className="row d-flex justify-content-center mt-4">
            <h3>Welcome User {JSON.parse(localStorage["token"]).user_name}</h3>
            <div className="row d-flex justify-content-right ml-5 mt-1  ">
              <Link to="/logout">logout</Link>
            </div>

            <br />
          </div>
          <Form
            inline
            className="row d-flex justify-content-center mt-5"
            onSubmit={this.onSubmit}
          >
            <FormControl
              type="text"
              id="search"
              placeholder="Search"
              className="mr-sm-2"
              onChange={this.onChange}
              value={this.state.search}
            />
            <Button type="submit" variant="outline-primary">
              Search
            </Button>
            <Button
              variant="outline-danger"
              className="ml-2"
              onClick={this.listAll}
            >
              List All Product
            </Button>
          </Form>
          <br />
        </div>
        {this.state.submit && (
          <div className="searchResult row d-flex justify-content-center mt-4">
            <div className="m-4">
              <select onChange={this.sortProduct}>
                <option value="" selected disabled hidden>
                  Sort By(Desc. Order)
                </option>
                <option value="price">Price</option>
                <option value="quantity">Quantity</option>
                <option value="rating">Rating of seller</option>
              </select>
            </div>
            <table>
              <tr>
                <th className="col-ml-2">Name of Item</th>
                <th className="col-ml-2">Name of Seller</th>
                <th className="col-ml-2">Quantity Available</th>
                <th className="col-ml-2">Price Per Item</th>
                <th className="col-ml-2">Average Rating</th>
                <th className="col-ml-2">Place Order</th>
              </tr>
              <tbody>
                {this.state.items.map((item, i) => {
                  return (
                    <tr>
                      <td>{item.name}</td>
                      <td>{item.seller}</td>
                      <td>{item.quantity}</td>
                      <td>{item.price / item.quantity}</td>
                      <td></td>
                      <td>
                        <Button type="submit">Buy Now</Button>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        )}
      </div>
    );
  }
}

export default UserDashboard;
