import React, { Component } from "react";
import axios from "axios";
import { Nav, Navbar, Form, Button, FormControl } from "react-bootstrap";

class navbar extends Component {
  constructor(props) {
    super(props);

    this.state = {
      authenticated: false
    };
    this.checkCurrent = this.checkCurrent.bind(this);
    this.onChange = this.onChange.bind(this);
    this.forceUpdate = this.forceUpdate.bind(this);
    window.location.reload();
  }

  onChange = e => {
    this.setState({ authenticated: this.checkCurrent() });
  };

  componentDidMount() {
    this.checkCurrent();
  }

  componentWillReceiveProps() {
    this.render();
  }

  checkCurrent() {
    try {
      var user = JSON.parse(localStorage["token"]).login;
      if (user === true) return JSON.parse(localStorage["token"]).user_name;
      else {
        return false;
      }
    } catch (e) {
      return false;
    }
  }

  render() {
    return (
      <div className="user-log" onChange={this.onChange} onLoad={this.onChange}>
        <Navbar bg="dark" variant="dark">
          <Navbar.Brand href="#home">IIIT-SHOPPING</Navbar.Brand>
          <Nav className="mr-auto">
            <Nav.Link href="/dashboard">Home</Nav.Link>
          </Nav>
          <Form inline>
            <Navbar.Text>{"WELCOME" + this.checkCurrent()}</Navbar.Text>
            <Nav.Link href="/logout">Logout</Nav.Link>
          </Form>
        </Navbar>
      </div>
    );
  }
}

export default navbar;
