import React, { Component } from "react";
import axios from "axios";
import { Nav, Navbar, Form, Button, FormControl } from "react-bootstrap";

class navbar extends Component {
  constructor() {
    super();
    this.checkCurrent = this.checkCurrent.bind(this);
    this.onChange = this.onChange.bind(this);
  }

  onChange = e => {
    this.setState({ [e.target.className]: e.target.value });
  };

  componentDidMount() {
    this.render();
  }

  checkCurrent() {
    try {
      var user = JSON.parse(localStorage["token"]).login;
      if (user == true) return true;
      else {
        return false;
      }
    } catch (e) {
      console.log(e);
      return false;
    }
  }

  render() {
    return (
      <div className="app" onChange={this.onChange}>
        {!this.checkCurrent() && (
          <>
            <Navbar bg="dark" variant="dark">
              <Navbar.Brand href="#home">IIIT-SHOPPING</Navbar.Brand>
              <Nav className="mr-auto">
                <Nav.Link href="/dashboard">Home</Nav.Link>
              </Nav>
            </Navbar>
          </>
        )}
        {this.checkCurrent() && (
          <>
            <Navbar bg="dark" variant="dark">
              <Navbar.Brand href="#home">IIIT-SHOPPING</Navbar.Brand>
              <Nav className="mr-auto">
                <Nav.Link href="/dashboard">Home</Nav.Link>
              </Nav>
              <Form inline>
                <Navbar.Text>
                  WELCOME {JSON.parse(localStorage["token"]).user_name}
                </Navbar.Text>
                <Nav.Link href="/logout">Logout</Nav.Link>
              </Form>
            </Navbar>
          </>
        )}
      </div>
    );
  }
}

export default navbar;
