import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Button } from "react-bootstrap";

class dashboard extends Component {
  constructor(pros) {
    super(pros);

    this.state = {
      ratings: ""
    };

    this.checkUser = this.checkUser.bind(this);
    this.onChange = this.onChange.bind(this);
    this.checkToken = this.checkToken.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  onChange = e => {
    if (e.target.value > 10 || e.target.value < 1) {
      this.setState({ ratings: "" });
    } else {
      this.setState({ [e.target.id]: e.target.value });
    }
  };

  componentDidMount() {
    try {
      this.checkUser();
      this.checkToken();
    } catch (e) {
      console.log(e);
    }
  }

  onSubmit(e) {
    var Ratings = {
      id: JSON.parse(localStorage["review"]).order_id,
      buyer_id: JSON.parse(localStorage["token"]).user_name,
      seller_id: JSON.parse(localStorage["review"]).seller_id,
      rating: this.state.ratings
    };

    axios
      .post("http://localhost:4000/product/add", Product)
      .then(res => {
        if (res.status >= 200 && res.status < 300) {
          alert("Product Successfully Listed");
          this.props.history.push("dashboard");
        } else {
          alert("Please enter Valid data");
        }
      })
      .catch(e => {
        console.log(e);
      });
  }

  componentDidUpdate() {
    this.checkUser();
  }

  checkToken() {
    try {
      var userType = JSON.parse(localStorage["review"]);
      return true;
    } catch (e) {
      this.props.history.push("login");
    }
  }
  checkUser() {
    try {
      var userType = JSON.parse(localStorage["token"]).user_type;
      if (userType == "Customer") {
        return true;
      } else {
        return false;
      }
    } catch (e) {
      this.props.history.push("login");
    }
  }
  render() {
    {
      this.checkUser() && this.checkToken();
    }
    return (
      <div className="app">
        <form onSubmit={this.onSubmit}>
          <div>
            <label>Product Name : </label>
            <input
              name="order"
              readOnly
              value={JSON.parse(localStorage["review"]).item_name}
            ></input>
          </div>
          <div>
            <label>Seller Name : </label>
            <input
              name="seller"
              readOnly
              value={JSON.parse(localStorage["review"]).seller_id}
            ></input>
          </div>

          <div>
            <label>Provide Ratings(In 10) : </label>
            <input
              name="ratings"
              id="ratings"
              onChange={this.onChange}
              value={this.state.ratings}
              type="number"
              min="1"
              max="10"
            ></input>
          </div>

          <div>
            <Button variant="outline-success" type="submit">
              Submit ratings
            </Button>
          </div>
        </form>
      </div>
    );
  }
}

export default dashboard;
