import React, { Component } from "react";
import axios from "axios";
import { Nav, Navbar, Form, Button, FormControl } from "react-bootstrap";

class navbar extends Component {
  constructor() {
    super();

    this.state = {
      content: this.checkCurrent()
    };
    this.checkCurrent = this.checkCurrent.bind(this);
    this.onChange = this.onChange.bind(this);
    this.forceUpdate = this.forceUpdate.bind(this);
  }

  onChange = e => {
    this.props.onClick(this.state.content);
    this.setState({ content: this.checkCurrent() });
  };

  componentDidMount() {
    this.checkCurrent();
    this.forceUpdate();
  }

  checkCurrent() {
    try {
      var user = JSON.parse(localStorage["token"]).login;
      if (user == true)
        return (
          <div className="user-log" onChange={this.onChange}>
            <Navbar bg="dark" variant="dark">
              <Navbar.Brand href="#home">IIIT-SHOPPING</Navbar.Brand>
              <Nav className="mr-auto">
                <Nav.Link href="/dashboard">Home</Nav.Link>
              </Nav>
              <Form inline>
                <Navbar.Text>
                  WELCOME {JSON.parse(localStorage["token"]).user_name}
                </Navbar.Text>
                <Nav.Link href="/logout">Logout</Nav.Link>
              </Form>
            </Navbar>
          </div>
        );
      else {
        return (
          <div>
            <>
              <Navbar bg="dark" variant="dark" onChange={this.onChange}>
                <Navbar.Brand href="#home">IIIT-SHOPPING</Navbar.Brand>
                <Nav className="mr-auto">
                  <Nav.Link href="/dashboard">Home</Nav.Link>
                </Nav>
              </Navbar>
            </>
          </div>
        );
      }
    } catch (e) {
      console.log(e);
      return (
        <div>
          <>
            <Navbar bg="dark" variant="dark" onChange={this.onChange}>
              <Navbar.Brand href="#home">IIIT-SHOPPING</Navbar.Brand>
              <Nav className="mr-auto">
                <Nav.Link href="/dashboard">Home</Nav.Link>
              </Nav>
            </Navbar>
          </>
        </div>
      );
    }
  }

  render() {
    return (
      <div id="app" onChange={this.onChange}>
        {this.state.content}
      </div>
    );
  }
}

export default navbar;
