import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Jumbotron } from "react-bootstrap";
import Axios from "axios";

class Orders extends Component {
  constructor(pros) {
    super(pros);

    this.state = {
      orderspresent: false,
      orders: []
    };

    this.checkUser = this.checkUser.bind(this);
    this.onChange = this.onChange.bind(this);
    this.fetchOrder = this.fetchOrder.bind(this);
  }

  onChange = e => {
    this.setState({ [e.target.className]: e.target.value });
  };

  fetchOrder = e => {
    e.preventDefault();

    Axios.get(
      "http://localhost:4000/order/fetch/" +
        JSON.parse(localStorage["token"]).user_name
    )
      .then(resp => {
        if (Object.keys(resp.data).length === 0) {
          alert("No orders found !");
          this.setState({ orderspresent: false });
        } else {
          this.setState({ orders: resp.data });
          //console.log(resp.data);
          if (Object.keys(this.state.items).length != 0) {
            this.setState({ orderspresent: true });
          }
        }
      })
      .catch(e => {
        console.log(e);
      });
  };

  componentDidMount() {
    try {
      this.checkUser();
    } catch (e) {
      console.log(e);
    }
  }

  componentDidUpdate() {
    this.checkUser();
  }
  checkUser() {
    try {
      var userType = JSON.parse(localStorage["token"]).user_type;
      if (userType == "Customer") {
        return true;
      } else {
        return false;
      }
    } catch (e) {
      this.props.history.push("login");
    }
  }
  render() {
    return (
      <div className="app">
        <Jumbotron>
          <div className="text-center">
            <h2>Past Orders</h2>
          </div>
        </Jumbotron>
      </div>
    );
  }
}

export default Orders;
