import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Jumbotron } from "react-bootstrap";
import Axios from "axios";

class Orders extends Component {
  constructor(pros) {
    super(pros);

    this.state = {
      orderspresent: false,
      orders: []
    };

    this.checkUser = this.checkUser.bind(this);
    this.onChange = this.onChange.bind(this);
    this.fetchOrder = this.fetchOrder.bind(this);
    this.fetchRemaining = this.fetchRemaining.bind(this);
  }

  onChange = e => {
    this.setState({ [e.target.className]: e.target.value });
  };

  fetchRemaining(prod_id) {
    var order = {
      id: prod_id
    };
  }

  fetchOrder = e => {
    console.log("HI");
    Axios.get(
      "http://localhost:4000/order/fetch/" +
        JSON.parse(localStorage["token"]).user_name
    )
      .then(resp => {
        if (Object.keys(resp.data).length === 0) {
          alert("No orders found !");
          this.setState({ orderspresent: false });
        } else {
          this.setState({ orders: resp.data });
          if (Object.keys(this.state.orders).length != 0) {
            this.setState({ orderspresent: true });
          }
        }
      })
      .catch(err => {
        console.log(err);
      });
  };

  componentDidMount() {
    try {
      this.checkUser();
      this.fetchOrder();
    } catch (e) {
      console.log(e);
    }
  }

  componentDidUpdate() {
    this.checkUser();
  }
  checkUser() {
    try {
      var userType = JSON.parse(localStorage["token"]).user_type;
      if (userType == "Customer") {
        return true;
      } else {
        return false;
      }
    } catch (e) {
      this.props.history.push("login");
    }
  }
  render() {
    return (
      <div className="app">
        <Jumbotron>
          <div className="text-center">
            <h2>Past Orders</h2>
          </div>
        </Jumbotron>
        <div>
          {this.state.orderspresent && (
            <table className="text-center">
              <tr>
                <th>Prod. Name</th>
                <th>Prod. Quantity</th>
                <th>Price of Order</th>
                <th>Seller</th>
                <th>Status</th>
              </tr>
              <tbody>
                {this.state.orders.map((order, i) => {
                  return (
                    <tr>
                      <td>{order.name}</td>
                      <td>{order.quantity}</td>
                      <td>{order.price}</td>
                      <td>{order.seller}</td>
                      <td>{order.status}</td>
                      <td>
                        {order.status === "pending"
                          ? this.fetchRemaining(order.prod_id)
                          : ""}
                      </td>
                      <td>
                        {order.status === "Dispatched" ? (
                          <a href="/review">Review</a>
                        ) : (
                          ""
                        )}
                      </td>
                      <td>
                        {order.status === "Dispatched" ? (
                          <a href="/ratings">Rate Order</a>
                        ) : (
                          ""
                        )}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          )}
        </div>
      </div>
    );
  }
}

export default Orders;
