import React, { Component } from "react";
import { Link } from "react-router-dom";
import Axios from "axios";

class review extends Component {
  constructor(pros) {
    super(pros);

    this.state = {
      seller_name: "",
      ratings: [],
      rating_avail: false,
      avg_rating: 0
    };
    this.onChange = this.onChange.bind(this);
  }

  fetchRatings() {
    console.log(JSON.parse(localStorage["seller"]).seller_name);
    Axios.get(
      "http://localhost:4000/ratings/" +
        JSON.parse(localStorage["seller"]).seller_name
    )
      .then(resp => {
        if (Object.keys(resp.data).length === 0) {
          alert("No ratings found !");
          this.setState({ ratings: [] });
        } else {
          //console.log(resp.data);
          this.setState({ ratings: resp.data });

          var int_rating = 0;

          for (var i = 0; i < resp.data.length; i++) {
            int_rating += resp.data[i].rating;
          }

          int_rating /= resp.data.length;

          if (Object.keys(this.state.items).length != 0) {
            this.setState({ rating_avail: true });
          }
        }
      })
      .catch(e => {
        console.log(e);
      });
  }

  onChange = e => {
    this.setState({ [e.target.className]: e.target.value });
  };

  componentDidMount() {
    try {
      this.setState({
        seller_name: JSON.parse(localStorage["seller"]).seller_name
      });
      this.fetchRatings();
    } catch (e) {
      this.props.history.push("login");
      console.log(e);
    }
  }

  render() {
    return (
      <div className="app">
        <div className="mt-5">
          <div className="row d-flex justify-content-center mt-5">
            <h3>SELLER DETAILS</h3>
          </div>
          <div>
            <br></br>
            <div className="row d-flex justify-content-center mt-5">
              <h3>SELLER NAME</h3>
              <h3 className="ml-4">{this.state.seller_name}</h3>
            </div>

            <div>
              <table>
                <tr>
                  <th>Ratings</th>
                </tr>
                <tbody>
                  {this.state.ratings.map((rating, i) => {
                    return (
                      <tr>
                        <td className="mt-5">{rating.ratings}</td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default review;
